// 得到当选中的label的URL加上条件
function getFilterURL(selectCell, where)
{ 
	if (isStringNull(where) && selectCell != null)
	{ 
		var values = where.split(';');
		var filter = $(selectCell).attr("filter"); 
		if (values.length > 0 && filter.length > 0)
		{
			for (var j = 0; j < values.length; j++)
			{
				while (filter.indexOf('{' + j + '}') > 0) // 去掉所有的参数{0}{0}....
				{
					filter = filter.replace('{' + j + '}', values[j]);
				}
			}
			return getUrl($(selectCell).attr("url")) + filter;
		} else
		{
			return $(selectCell).attr("url");
		}
	}
	return '';
}

// 去掉默认的条件(用于label)
function getUrl(url)
{
	var params = url.split('&');
	for (i = 0; i < params.length; i++)
	{
		if (params[i].indexOf('filter=') == 0)
			return url.replace("&" + params[i], '');
	}
	return url;
}
