tinymce.PluginManager.add('jbimages', function(editor, url) {
	function jbBox() {
		editor.windowManager.open( {
			title : '插入文件或图片(双击下表中数据插入)',
			file : url + '/dialog-v4.jsp',
			width : 420,
			height : 405
		})
	}
	editor.addButton('jbimages', {
		tooltip : 'Upload an image',
		icon : 'image',
		text : '',
		onclick : jbBox
	});
	editor.addMenuItem('jbimages', {
		text : 'Upload image',
		icon : 'image',
		context : 'insert',
		onclick : jbBox
	})
});